import logging
from rest_framework.views import APIView
from rest_framework.response import Response
from rest_framework import status
from django.conf import settings
from ..models import Panel, Inverter, variableCosts, BracketCosts
from ..middleware.error_handler import AppError, ErrorTypes
import math

logger = logging.getLogger(__name__)

class QuoteGenerateAPIView(APIView):
    """API endpoint to generate solar system quotes based on bill data."""

    def post(self, request):
        try:
            bill_data = request.data
            if not bill_data:
                raise AppError(
                    message="Bill data is required",
                    code=ErrorTypes.VALIDATION_ERROR
                )

            # Get system components
            try:
                panel = Panel.objects.get(default_choice=True)
            except Panel.DoesNotExist:
                raise AppError(
                    message="No default panel configuration found",
                    code=ErrorTypes.VALIDATION_ERROR
                )

            # Calculate system size
            yearly_units = float(bill_data.get('Total Yearly Units', 0))
            daily_avg = yearly_units / 365
            system_size_kw = daily_avg / 4
            system_size = math.ceil(system_size_kw * 1.5)

            # Calculate panels needed
            panels_needed = math.ceil((system_size * 1000) / panel.power)

            # Get appropriate inverter
            inverter = Inverter.objects.filter(
                power__gte=system_size, 
                availability=True
            ).order_by('power').first()

            if not inverter:
                raise AppError(
                    message="No suitable inverter found for this system size",
                    code=ErrorTypes.VALIDATION_ERROR,
                    data={"system_size": system_size}
                )

            # Get costs
            try:
                costs = self._get_costs(system_size)
            except AppError:
                raise
            except Exception as e:
                logger.exception("Error fetching costs")
                raise AppError(
                    message="Failed to retrieve cost information",
                    code=ErrorTypes.SERVER_ERROR
                )

            # Calculate production and costs
            calculations = self._calculate_system_metrics(
                system_size=system_size,
                panel_cost=panel.price,
                panels_needed=panels_needed,
                inverter_cost=inverter.price,
                costs=costs
            )

            quote_data = {
                "systemDetails": {
                    "systemSize": system_size,
                    "panelCount": panels_needed,
                    "panelType": panel.brand,
                    "inverterType": inverter.brand,
                    "roofArea": panels_needed * 2,
                    "installationTime": "3-5 days",
                    "warranty": "25 years"
                },
                "production": calculations['production'],
                "financial": calculations['financial'],
                "environmental": calculations['environmental']
            }

            return Response({
                "success": True,
                "data": quote_data
            })

        except AppError:
            raise
        except Exception as e:
            logger.exception("Error generating quote")
            raise AppError(
                message="Failed to generate quote",
                code=ErrorTypes.SERVER_ERROR,
                data={'original_error': str(e)} if settings.DEBUG else {}
            )

    def _get_costs(self, system_size):
        """Retrieve all necessary costs for quote calculation."""
        try:
            # Get variable costs
            net_metering = variableCosts.objects.get(cost_name='Net Metering').cost
            installation_cost = variableCosts.objects.get(cost_name='Installation Cost per Watt').cost
            frame_cost = variableCosts.objects.get(cost_name='Frame Cost per Watt').cost
            labor_cost = variableCosts.objects.get(cost_name='Labor Cost').cost

            # Calculate size-dependent costs
            total_installation_cost = installation_cost * system_size * 1000
            total_frame_cost = frame_cost * system_size * 1000
            total_labor_cost = labor_cost * system_size * 1000

            # Get bracket costs
            dc_cable_cost = BracketCosts.objects.filter(
                Type='DC Cables',
                SystemRange__lte=system_size
            ).order_by('-SystemRange').first()
            
            ac_cable_cost = BracketCosts.objects.filter(
                Type='AC Cables',
                SystemRange__lte=system_size
            ).order_by('-SystemRange').first()
            
            accessories_cost = BracketCosts.objects.filter(
                Type='Accessories',
                SystemRange__lte=system_size
            ).order_by('-SystemRange').first()

            if not all([dc_cable_cost, ac_cable_cost, accessories_cost]):
                raise AppError(
                    message="Missing bracket costs for system size",
                    code=ErrorTypes.VALIDATION_ERROR,
                    data={"system_size": system_size}
                )

            return {
                'net_metering': net_metering,
                'installation': total_installation_cost,
                'frame': total_frame_cost,
                'labor': total_labor_cost,
                'dc_cable': dc_cable_cost.cost,
                'ac_cable': ac_cable_cost.cost,
                'accessories': accessories_cost.cost
            }

        except variableCosts.DoesNotExist as e:
            raise AppError(
                message="Missing cost configuration",
                code=ErrorTypes.VALIDATION_ERROR
            )

    def _calculate_system_metrics(self, system_size, panel_cost, panels_needed, inverter_cost, costs):
        """Calculate system production and financial metrics."""
        # Production calculations
        daily_production = system_size * 4  # 4 kWh per kW per day average
        monthly_production = daily_production * 30
        yearly_production = daily_production * 365

        # Cost calculations
        total_panel_cost = panels_needed * panel_cost
        total_cost = sum([
            total_panel_cost,
            inverter_cost,
            costs['net_metering'],
            costs['installation'],
            costs['frame'],
            costs['dc_cable'],
            costs['ac_cable'],
            costs['accessories'],
            costs['labor']
        ])

        # Calculate savings (assuming PKR 20 per unit)
        annual_savings = yearly_production * 20
        monthly_savings = monthly_production * 20

        return {
            'production': {
                'daily': round(daily_production, 1),
                'monthly': self._generate_monthly_production(monthly_production),
                'annual': round(yearly_production),
                'peakHours': 4.5,
                'performanceRatio': 0.75
            },
            'financial': {
                'systemCost': round(total_cost),
                'annualSavings': round(annual_savings),
                'monthlySavings': round(monthly_savings),
                'paybackPeriod': round(total_cost / annual_savings, 1),
                'roi': round((annual_savings / total_cost) * 100, 1),
                'savingsTimeline': self._generate_savings_timeline(total_cost, annual_savings)
            },
            'environmental': {
                'co2Offset': round(system_size * 1.2, 1),
                'treesEquivalent': round(system_size * 20),
                'homesEquivalent': math.floor(yearly_production / 12000),
                'carbonFootprintReduction': round(system_size * 1000)
            }
        }

    def _generate_monthly_production(self, base_production):
        """Generate monthly production estimates with seasonal variations."""
        months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 
                'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec']
        seasonal_factors = {
            'winter': 0.7,
            'spring': 0.9,
            'summer': 1.2,
            'fall': 0.8
        }

        monthly_data = []
        for i, month in enumerate(months):
            if i < 2 or i == 11:
                factor = seasonal_factors['winter']
            elif i < 5:
                factor = seasonal_factors['spring']
            elif i < 8:
                factor = seasonal_factors['summer']
            else:
                factor = seasonal_factors['fall']

            production = round(base_production * factor)
            consumption = round(base_production * 0.9)
            monthly_data.append({
                "month": month,
                "production": production,
                "consumption": consumption
            })

        return monthly_data

    def _generate_savings_timeline(self, system_cost, annual_savings):
        """Generate 25-year savings timeline with inflation adjustment."""
        return [
            {
                "year": year + 1,
                "annualSavings": round(annual_savings * (1 + (year * 0.05))),
                "cumulativeSavings": round(annual_savings * (year + 1) * (1 + (year * 0.025)))
            }
            for year in range(25)
        ]


